      url: `groups?Nome=${student}`
    })));
    this.groups$ = merge(this.allGroups$, this.filterGroups$);
    this._form = new FormGroup({
      Nome: new FormControl(''),
      Email: new FormControl(''),
      Senha: new FormControl(''),
      CPF: new FormControl(''),
      DataNasc: new FormControl(''),
      NumRegistro: new FormControl('')
    });
  }

  ngOnInit() {
    if (this.dialogData.type === 'edit') {
      this.registerService.Get({
        url: `teachers?id=${this.dialogData.id}`
      }).subscribe(success => {
        this._form.patchValue(success[0]);
      });
    }
  }

  addToGroup(group) {
    if (group) {
      this.groupsList.push(group);
    }
  }

  removeGroup(group) {
    let index = this.groupsList.indexOf(group);

    if (index) {
      this.groupsList.splice(index, 1);
    }
  }

  registerTeacher() {
    this.registerService.Post({
      url: `teachers`,
      body: this._form.value
    }).subscribe(success => {
      this.matSnackbar.open('Professor registrado com sucesso', 'Fechar', {
        duration: 1500
      }).afterDismissed().subscribe(() => this.DialogRef.close(true));
    }, error => {
      this.matSnackbar.open('Ocorreu um erro ao tentar registrar', 'Fechar', {
        duration: 2500
      });
    });
  }

  putTeacher() {
    this.registerService.Put({
      url: `teachers/${this.dialogData.id}`,
      body: this._form.value
    }).subscribe(success => {
      this.matSnackbar.open('Alterações salvas com sucesso', 'Fechar', {
        duration: 1500
      }).afterDismissed().subscribe(() => this.DialogRef.close(true));
    }, error => {
      this.matSnackbar.open('Ocorreu um erro. Tente novamente', 'Fechar', {
        duration: 2000
      });
    });
  }

}

